function setHeaderHeight(){const s=document.querySelector("main"),e=s.querySelector("h1"),t=document.querySelector(".sidebar-toggle"),o=document.querySelector("header"),n=document.querySelector('div[role="logo and breadcrumb"]');s&&t&&e&&requestAnimationFrame(()=>{if(o){const e=window.getComputedStyle(o),t=e.marginRight;document.documentElement.style.setProperty("--header-margin-right",t)}if(n){const e=window.getComputedStyle(n),t=n.offsetHeight,s=parseInt(e.marginBottom),o=t+s;document.documentElement.style.setProperty("--logo-section-height",`${o}px`)}if(window.innerWidth>=1560){const n=e.getBoundingClientRect();t.style.top=`${n.top+window.scrollY}px`}else t.style.removeProperty("top");const s=e.getBoundingClientRect(),i=s.bottom+window.scrollY+32;document.documentElement.style.setProperty("--toc-position",`${i}px`)})}function isMobile(){return window.innerWidth<1560}function initializeSidebar(){setHeaderHeight();const e=document.querySelector(".sidebar");e&&(isMobile()?e.classList.remove("active"):e.classList.add("active"))}function toggleSidebar(){const e=document.querySelector(".sidebar");e&&e.classList.toggle("active")}document.querySelectorAll(".sidebar a").forEach(e=>{e.addEventListener("click",()=>{if(isMobile()){const e=document.querySelector(".sidebar");e&&e.classList.remove("active")}})}),document.addEventListener("DOMContentLoaded",initializeSidebar),window.addEventListener("resize",initializeSidebar),window.addEventListener("resize",setHeaderHeight)