function setHeaderHeight(){const t=document.querySelector("main"),n=t.querySelector("h1"),o=document.querySelector(".sidebar-toggle"),s=document.querySelector("header"),e=document.querySelector('div[role="logo and breadcrumb"]');t&&o&&n&&requestAnimationFrame(()=>{if(s){const e=window.getComputedStyle(s),t=e.marginRight;document.documentElement.style.setProperty("--header-margin-right",t)}if(e){const t=window.getComputedStyle(e),n=e.offsetHeight,s=parseInt(t.marginBottom),o=n+s;document.documentElement.style.setProperty("--logo-section-height",`${o}px`)}const t=n.getBoundingClientRect(),o=t.top+window.scrollY+20;document.documentElement.style.setProperty("--toc-position",`${o}px`)})}function isMobile(){return window.innerWidth<1560}function initializeSidebar(){setHeaderHeight();const e=document.querySelector(".sidebar");e&&(isMobile()?e.classList.remove("active"):e.classList.add("active"))}function toggleSidebar(){const e=document.querySelector(".sidebar");e&&e.classList.toggle("active")}document.querySelectorAll(".sidebar a").forEach(e=>{e.addEventListener("click",()=>{if(isMobile()){const e=document.querySelector(".sidebar");e&&e.classList.remove("active")}})}),document.addEventListener("DOMContentLoaded",initializeSidebar),window.addEventListener("resize",initializeSidebar),window.addEventListener("resize",setHeaderHeight)